// import components
import Tweet from '../Components/Tweet';
import NewTweet from '../Components/NewTweet';


function Main () {
    const [ numOfTweets, setNumOfTweets ] = useState(0);
    const [ showNewTweetTemplate, setShowNewTweetTemplate ] = useState(false);

    const setNewTweetDisplay = () => {
        // showNewTweetTemplate ? setShowNewTweetTemplate(false) : setShowNewTweetTemplate(true)
    };

    return (
        <main>
            {/* search */ }
            <input
                id="searchInput"
                placeholder="Filter tweets"
            ></input>
            {/* panel */ }
            <div id="panel">
                <div>Showing { numOfTweets } tweets</div>
                <div onClick={ setNewTweetDisplay }>Add a new tweet</div>
            </div>
            {/* new tweet */ }
            <NewTweet />
            {/* map - container of tweets */ }
            <div id="tweetsList">
                <Tweet />
            </div>
        </main>
    );
}

export default Main;
