import React from 'react';

function Newtweet ({ addPost }) {
    const addNewTweet = (e) => {

        // addPost({
        //     title: e.target.newTweetTitle.value,
        //     text: e.target.newTweetText.value,
        //     date: new Date()
        // });
        const x = {
            title: e.target.newTweetTitle.value,
            text: e.target.newTweetText.value,
            date: new Date().toDateString()
        };

        // console.log(x);
        // alert("");

        addPost(x);

        // send data to DB
    };

    return (
        <div id="newTweet">
            <input
                type="text"
                name="newTweetTitle"
                placeholder="Title"
                required></input>
            <textarea
                name="newTweetText"
                placeholder="Tweet text [maximum 256 characters]"
                maxLength="256"
                required></textarea>
            <input
                // type="submit"
                value="Post a new tweet" />
        </div>
    );
}

export default Newtweet;
