import React from 'react';

function Newtweet ({ prop }) {
    const addNewTweet = (e) => {
        // e.target.newTweetTitle.value
        // e.target.newTweetText.value
        // pass prop to component and make the fucntion here

        prop.test("hello", "world");
    };

    return (
        <form id="newTweet" onSubmit={ addNewTweet }>
            <input
                type="text"
                name="newTweetTitle"
                placeholder="Title"
                required></input>
            <textarea
                name="newTweetText"
                placeholder="Tweet text [maximum 256 characters]"
                maxLength="256"
                required></textarea>
            <input
                type="submit"
                value="Post a new tweet" />
        </form>
    );
}

export default Newtweet;
